import { FormEvent, useState } from 'react';
import { useHistory } from 'react-router-dom';

import illustrationImg from '../assets/images/illustration.svg';
import logoImg from '../assets/images/logo.svg';
import googleIconImg from '../assets/images/google-icon.svg'
import logInImg from '../assets/images/log-in.svg';

import { database } from 'data/services/firebase';
import { Button } from '../components';
import { useAuth } from 'main/hooks';

import '../styles/auth.scss';
import { Box, Flex, Image, Text } from '@chakra-ui/react';

export function Home() {
  const history = useHistory();
  const { user, signInWithGoogle } = useAuth();
  const [roomCode, setRoomCode] = useState('');

  async function handleCreateRoom() {
    if (!user) {
      await signInWithGoogle();
    };

    history.push('/rooms/new');
  }

  async function handleJoinRoom(event: FormEvent) {
    event.preventDefault();

    if (roomCode.trim() === '') {
      return;
    }

    const roomRef = await database.ref(`rooms/${roomCode}`).get();

    if (!roomRef.exists()) {
      alert('A sala não existe');
      return;
    }

    if (roomRef.val().endedAt) {
      alert('A sala já foi fechada');
      return;
    }

    history.push(`/rooms/${roomCode}`);
  }

  return (
    <Flex >
      <Flex justify="center" align="center" direction="column" w="50%" h="100vh" as="aside" background="gray.800">
        <Image src={illustrationImg} width="40%" alt="Ilustração simbolizando perguntas e respostas" />
        <Box>
          <Text fontSize={24} fontWeight={700} lineHeight={4}>Crie salas de Q&amp;A ao vivo</Text>
          <Text>Tire as dúvidas da sua audiência em tempo real</Text>
        </Box>
      </Flex>
      <main>
        <div className="main-content">
          <img src={logoImg} alt="letmeask" />
          <button className="create-room" onClick={handleCreateRoom}>
            <img src={googleIconImg} alt="Logo do Google" />
            Crie sua sala com o Google
          </button>
          <div className="separator">ou entre em uma sala</div>
          <form onSubmit={handleJoinRoom}>
            <input
              type="text"
              placeholder="Digite o código da sala"
              onChange={event => setRoomCode(event.target.value)}
              value={roomCode}
            />
            <Button type="submit">
              <img src={logInImg} alt="Ícone de entrar" />
              Entrar na sala
            </Button>
          </form>
        </div>
      </main>
    </Flex>
  )
}
